function vendor1() {
  let vendor =
    "وندور سفارش را برای مشتری ارسال کردند. تغییرات لازم روی بکند اعمال شد. لطفا بررسی شود با تشکر";
  navigator.clipboard.writeText(vendor);
  alert("پیش نویس شما کپی شد!");
}
